const typeDefs = gql`
  type Resident {
      _id: String!
      identity_document: Int
      name: String!
      birth_date: String
      admission_date: String!
      gender: String!
      state: String
      contact_name: String
      contact_phone: Int
      diseases: String
  }

  type Query {
    resident(idNumber: String!): Resident
    residents: [Resident]
  }
`;

const resolvers = {
  Query: {
    resident: (root, { idNumber }, { dataSources }) =>
      dataSources.mvrpAPI.getAResident(idNumber),
    residents: (root, args, { dataSources }) => dataSources.mvrpAPI.getAllResidents(),
  },
  Resident: {
    identity_document: ({ idDoc }) => idDoc,
    diseases: ({ reportedDiseases }) => reportedDiseases,
  },
};

const server = new ApolloServer({
  typeDefs,
  resolvers,
  dataSources: () => ({
    mvrpAPI: new MvrpAPI(),
  }),
});

