const typeDefs = gql`
    type Resident {
        _id: String!
        identity_document: Int
        name: String!
        birth_date: String
        admission_date: String!
        gender: String!
        state: String
        contact_name: String
        contact_phone: Int
        diseases: String
    }

    type Report_type {
        id: Int!
        Type: String!
    }

    type Report {
        id: Int!
        report_type_id: Report_type
        created_at: String
        IP: String
    }

    type Query {
        resident(idNumber: String!): Resident
        residents: [Resident]
        reports: [Report]
    }
`;

const resolvers = {
    Query: {
        resident: (root, { idNumber }, { dataSources }) => dataSources.ResidentAPI.getAResident(idNumber),
        residents: (root, args, { dataSources }) => dataSources.ResidentAPI.getAllResidents(),
        resident: (root, { idNumber }, { dataSources }) => dataSources.ReportsAPI.getAReport(idNumber),
        residents: (root, args, { dataSources }) => dataSources.ReportsAPI.getAllReports(),
        report_types: (root, args, { dataSources }) => dataSources.ReportsAPI.getAllTypes(),
    },
    Resident: {
        identity_document: ({ idDoc }) => idDoc,
        diseases: ({ reportedDiseases }) => reportedDiseases,
    },
};

const server = new ApolloServer({
    typeDefs,
    resolvers,
    dataSources: () => ({
        ResidentAPI: new ResidentAPI(),
        ReportsAPI: new ReportsAPI(),
    }),
});

